import React,{Component} from 'react'
import { Button, Form ,Input, Message } from 'semantic-ui-react'
import {Link} from '../../../routes';
import Layout from '../../../components/Layout';

//import web3 from '../ethereum/web3';
//import factory from '../ethereum/factory';

class CampaignRequestNew extends Component{

    state={
        description:'',
        value:'',
        recipientAdd:'',
        errorMsg:'',
        loading:''
    };

    onSubmit = async ()=>{
        this.setState({errorMsg:''});
        this.setState({loading:true});
        try{
            event.preventDefault();
            //得到合约地址
            const camp = Campaign(this.props.address);
            const accounts = await web3.eth.getAccounts();

            await camp.methods.contribute().send({
                from:accounts[0],
                value:web3.utils.toWei(this.state.value,'ether')
            })
            Router.replaceRoute(`/campaigns/${this.props.address}`);
        }catch(err){
            this.setState({errorMsg:err.message});
        }
        this.setState({loading:false});
    }
    
    render(){
        return (
            <Layout>
              <h3>增加请求列表</h3>
              <Form onSubmit={this.onSubmit} error={!!this.state.errorMsg}>
                <Form.Field>
                  <label>请求描述</label>
                  <Input 
                     value={this.state.description}
                     onChange={event=>this.setState({description:event.target.value})}
                  />
                </Form.Field>
                <Form.Field>
                  <label>请求金额</label>
                  <Input 
                    value={this.state.value}
                    onChange={event=>this.setState({value:event.target.value})}
                 />
                </Form.Field>
                <Form.Field>
                  <label>受益人地址</label>
                  <Input 
                    value={this.state.recipientAdd}
                    onChange={event=>this.setState({recipientAdd:event.target.value})}
                  />
                </Form.Field>
                <Message error header="错误！" content={this.state.errorMsg}/>
                <Button loading={this.state.loading} primary>投资</Button>
              </Form>
            </Layout>
          );

    }

}

export default CampaignRequestNew;

