// JavaScript source code
import React,{Component} from 'react';
import { Form , Input, Button } from 'semantic-ui-react';
import Campaign from '../ethereum/Campaign';
import web3 from '../ethereum/web3';

class ContributeForm extends Component{

    state = {
        value:''
    };
    onSubmit = async ()=>{
        event.preventDefault();
        //得到合约地址
        const camp = Campaign(this.props.address);
        const accounts = await web3.eth.getAccounts();

        await camp.methods.contribute().send({
            from:accounts[0],
            value:web3.utils.toWei(this.state.value,'ether')
        })
    }

    render(){
        return (
            <Form onSubmit={this.onSubmit}>
              <Form.Field>
                <label>总的投资额度</label>
                <Input value={this.state.value} onChange={event=>setState({value:event.target.value})}
                    label="ether" labelPosition="right" />
              </Form.Field>
              <Button primary>投资</Button>
            </Form>
         );
    }
}
export default ContributeForm;
