import web3 from '../ethereum/web3';
import factory from '../ethereum/factory';

class CampaignIndex extends Component{

    //在Next服务器中预处理JS脚本，NEXT生命周期函数
    static async getInitialProps(){
        assert('1');
        const campaign = 'asfasdgfsfgjhgkhfdsafghjkjhgf';//await factory.methods.getDeployedCampaign().call();
        //返回后存储在Props属性中
        assert(campaign);
        return {campaign};
    }
//    async componentDidMount(){
//        const campaign = await factory.methods.getDeployedCampaign().call();
//        console.log(campaign);
//    }
    renderCampaign(){
        const item = this.props.campaign.map(address=>{
            return {
                header: address,
                description: <a>查看众筹信息</a>,
                fluid:true
            }
        });
        return <Card.Group items={item} />;
    }

    render(){
            return (
                <div>
                     <link rel="stylesheet" href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css" />
                    {this.props.campaign}
                </div>
                );
    }
}

export default CampaignIndex;
